import React, { useState, useEffect, useCallback } from 'react';

interface CoinDropProps {
  isActive: boolean;
  clickCount: number;
}

const CoinDrop: React.FC<CoinDropProps> = ({ isActive, clickCount }) => {
  const [coins, setCoins] = useState<{ id: number; x: number; y: number; speed: number }[]>([]);

  const addCoins = useCallback(() => {
    const newCoins = Array.from({ length: clickCount }, (_, index) => ({
      id: Date.now() + index,
      x: Math.random() * 100,
      y: -20,
      speed: 2 + Math.random() * 3,
    }));
    setCoins(prevCoins => [...prevCoins, ...newCoins]);
  }, [clickCount]);

  useEffect(() => {
    if (isActive) {
      addCoins();
    }
  }, [isActive, addCoins]);

  useEffect(() => {
    const interval = setInterval(() => {
      setCoins(prevCoins =>
        prevCoins
          .map(coin => ({ ...coin, y: coin.y + coin.speed }))
          .filter(coin => coin.y < 120)
      );
    }, 50);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="coin-drop">
      {coins.map(coin => (
        <div
          key={coin.id}
          className="coin"
          style={{
            left: `${coin.x}%`,
            top: `${coin.y}%`,
          }}
        />
      ))}
    </div>
  );
};

export default CoinDrop;
