'use client'
import React, { useState, useEffect } from 'react';
import CoinDrop from './NeonThunder';
import { TonConnectButton, useTonConnect, useTonWallet } from '@tonconnect/ui-react';

const upgradeData = [
  { level: 1, name: "Free", hashrate: "2 GH/s", rentPeriod: 90, rentPrice: 0, dailyReturn: 0.02 },
  { level: 2, name: "TON Silver", hashrate: "20 GH/s", rentPeriod: 90, rentPrice: 1, dailyReturn: 0.2 },
  { level: 3, name: "TON Gold", hashrate: "100 GH/s", rentPeriod: 90, rentPrice: 5, dailyReturn: 1 },
  { level: 4, name: "TON Diamond", hashrate: "600 GH/s", rentPeriod: 90, rentPrice: 25, dailyReturn: 6 },
  { level: 5, name: "TON Platinum", hashrate: "1300 GH/s", rentPeriod: 90, rentPrice: 50, dailyReturn: 13 },
  { level: 6, name: "TON VIP", hashrate: "3000 GH/s", rentPeriod: 90, rentPrice: 100, dailyReturn: 30 },
];

interface UpgradePlan {
  level: number;
  name: string;
  hashrate: string;
  rentPeriod: number;
  rentPrice: number;
  dailyReturn: number;
}

interface UpgradeMinerPopupProps {
  isOpen: boolean;
  onClose: () => void;
  onSelectPlan: (plan: UpgradePlan) => void;
  currentLevel: number;
  currentTon: number;
}

const UpgradeMinerPopup: React.FC<UpgradeMinerPopupProps> = ({ isOpen, onClose, onSelectPlan, currentLevel, currentTon }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-gray-800 p-6 rounded-lg max-w-4xl w-full">
        <h2 className="text-xl font-bold mb-4 text-white">Upgrade Mining Power</h2>
        <div className="grid grid-cols-3 gap-4">
          {upgradeData.map((plan) => (
            <button
              key={plan.level}
              onClick={() => onSelectPlan(plan)}
              className={`p-4 rounded-lg text-left ${
                plan.level <= currentLevel
                  ? 'bg-gray-700 text-gray-400 cursor-not-allowed'
                  : currentTon >= plan.rentPrice
                  ? 'bg-blue-600 hover:bg-blue-700'
                  : 'bg-gray-600 text-gray-300 cursor-not-allowed'
              }`}
              disabled={plan.level <= currentLevel || currentTon < plan.rentPrice}
            >
              <div className="font-bold">{plan.name}</div>
              <div>Hashrate: {plan.hashrate}</div>
              <div>Rent Period: {plan.rentPeriod} days</div>
              <div>Rent Price: {plan.rentPrice} TON</div>
              <div>Daily Return: {plan.dailyReturn} TON</div>
            </button>
          ))}
        </div>
        <button onClick={onClose} className="mt-4 bg-gray-600 text-white px-4 py-2 rounded hover:bg-gray-700">
          Close
        </button>
      </div>
    </div>
  );
};

const CryptoMiningApp: React.FC = () => {
  const [currentTab, setCurrentTab] = useState('Home');
  const [currentTon, setCurrentTon] = useState(0);
  const [minedTon, setMinedTon] = useState(0);
  const [shib, setShib] = useState(0);
  const [taps, setTaps] = useState(0);
  const [upgradeLevel, setUpgradeLevel] = useState(1);
  const [isMining, setIsMining] = useState(false);
  const [showCoinDrop, setShowCoinDrop] = useState(false);
  const [isShaking, setIsShaking] = useState(false);
  const [isUpgradePopupOpen, setIsUpgradePopupOpen] = useState(false);
  const [selectedPlan, setSelectedPlan] = useState<UpgradePlan | null>(null);

  const { connected } = useTonConnect();
  const wallet = useTonWallet();

  const currentUpgrade = upgradeData[upgradeLevel - 1];

  useEffect(() => {
    const interval = setInterval(() => {
      setMinedTon(prevTon => prevTon + currentUpgrade.dailyReturn / (24 * 60 * 60));
    }, 1000);
    return () => clearInterval(interval);
  }, [upgradeLevel]);

  const claimTon = () => {
    setCurrentTon(prevTon => prevTon + minedTon);
    setMinedTon(0);
  };

  const handleTap = () => {
    setTaps(prevTaps => prevTaps + 1);
    setMinedTon(prevTon => prevTon + currentUpgrade.dailyReturn / (24 * 60 * 60));
    setIsMining(true);
    setShowCoinDrop(true);
    setIsShaking(true);
    setTimeout(() => {
      setIsMining(false);
      setIsShaking(false);
    }, 100);
    setTimeout(() => {
      setShowCoinDrop(false);
    }, 1000);
  };

  const handleUpgrade = (plan: UpgradePlan) => {
    if (connected && wallet) {
      // Here you would typically interact with a smart contract to perform the upgrade
      // For this example, we'll just update the state
      if (currentTon >= plan.rentPrice && plan.level > upgradeLevel) {
        setCurrentTon(prevTon => prevTon - plan.rentPrice);
        setUpgradeLevel(plan.level);
        setIsUpgradePopupOpen(false);
        setSelectedPlan(null);
      }
    } else {
      alert("Please connect your TON wallet to upgrade.");
    }
  };

  const TabButton: React.FC<{ icon: React.FC, label: string }> = ({ icon: Icon, label }) => (
    <button
      onClick={() => setCurrentTab(label)}
      className={`flex flex-col items-center justify-center w-1/4 py-2 ${
        currentTab === label ? 'text-blue-500' : 'text-gray-500'
      }`}
    >
      <Icon />
      <span className="text-xs mt-1">{label}</span>
    </button>
  );

  const HomeIcon: React.FC = () => (
    <svg viewBox="0 0 24 24" width="20" height="20" stroke="currentColor" strokeWidth="2" fill="none">
      <path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z"></path>
      <polyline points="9 22 9 12 15 12 15 22"></polyline>
    </svg>
  );

  const UsersIcon: React.FC = () => (
    <svg viewBox="0 0 24 24" width="20" height="20" stroke="currentColor" strokeWidth="2" fill="none">
      <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"></path>
      <circle cx="9" cy="7" r="4"></circle>
      <path d="M23 21v-2a4 4 0 0 0-3-3.87"></path>
      <path d="M16 3.13a4 4 0 0 1 0 7.75"></path>
    </svg>
  );

  const ClipboardIcon: React.FC = () => (
    <svg viewBox="0 0 24 24" width="20" height="20" stroke="currentColor" strokeWidth="2" fill="none">
      <path d="M16 4h2a2 2 0 0 1 2 2v14a2 2 0 0 1-2 2H6a2 2 0 0 1-2-2V6a2 2 0 0 1 2-2h2"></path>
      <rect x="8" y="2" width="8" height="4" rx="1" ry="1"></rect>
    </svg>
  );

  const FileTextIcon: React.FC = () => (
    <svg viewBox="0 0 24 24" width="20" height="20" stroke="currentColor" strokeWidth="2" fill="none">
      <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"></path>
      <polyline points="14 2 14 8 20 8"></polyline>
      <line x1="16" y1="13" x2="8" y2="13"></line>
      <line x1="16" y1="17" x2="8" y2="17"></line>
      <polyline points="10 9 9 9 8 9"></polyline>
    </svg>
  );

  const HomeTab: React.FC = () => (
    <div className="p-4 flex flex-col h-full">
      <div className="flex justify-between mb-4">
        <div className="flex items-center">
          <div className="w-8 h-8 bg-blue-500 rounded-full flex items-center justify-center text-white mr-2">
            <svg viewBox="0 0 24 24" width="20" height="20" stroke="currentColor" strokeWidth="2" fill="none">
              <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5" />
            </svg>
          </div>
          <div>
            <div className="text-sm font-bold">TON Balance</div>
            <div className="text-xs text-gray-400">{currentTon.toFixed(6)} TON</div>
          </div>
        </div>
        <TonConnectButton />
      </div>
      <div className="flex justify-between mb-4">
        <div className="flex items-center">
          <div className="w-8 h-8 bg-orange-500 rounded-full flex items-center justify-center text-white mr-2">
            <span className="text-xs">SHIB</span>
          </div>
          <div>
            <div className="text-sm font-bold">SHIB</div>
            <div className="text-xs text-gray-400">0.00 SHIB</div>
          </div>
        </div>
        <button className="bg-blue-500 text-white px-4 py-1 rounded text-sm">Withdraw</button>
      </div>
      <div className="flex-grow flex flex-col items-center justify-center relative">
        <div className="relative w-48 h-48 mb-4">
          <div className="absolute inset-0 bg-blue-900 rounded-full"></div>
          <div className="absolute inset-1 bg-black rounded-full flex items-center justify-center">
            <svg viewBox="0 0 200 200" width="100%" height="100%" className="animate-spin" style={{animationDuration: '3s'}}>
              <g>
                {[0, 45, 90, 135, 180, 225, 270, 315].map((rotation, index) => (
                  <path key={index} d="M100 100 L85 15 A70 70 0 0 1 115 15 Z" fill="url(#blade-gradient)" transform={`rotate(${rotation} 100 100)`} />
                ))}
              </g>
              <circle cx="100" cy="100" r="20" fill="url(#hub-gradient)" />
              <circle cx="100" cy="100" r="98" stroke="#3182CE" strokeWidth="4" fill="none" />
              <defs>
                <radialGradient id="blade-gradient">
                  <stop offset="0%" stopColor="#718096" />
                  <stop offset="100%" stopColor="#2D3748" />
                </radialGradient>
                <radialGradient id="hub-gradient">
                  <stop offset="0%" stopColor="#A0AEC0" />
                  <stop offset="100%" stopColor="#4A5568" />
                </radialGradient>
              </defs>
            </svg>
          </div>
        </div>
        <div className="text-center mb-4">
          <div className="text-2xl font-bold">{minedTon.toFixed(6)} TON</div>
          <div className="text-sm text-gray-400">Mined (Unclaimed)</div>
          <div className="text-sm text-gray-400">Hashrate: {currentUpgrade.hashrate}</div>
          <div className="text-sm text-gray-400">Taps: {taps}</div>
          <div className="text-sm text-gray-400">Daily Return: {currentUpgrade.dailyReturn} TON</div>
          <div className="text-sm text-gray-400">Current Plan: {currentUpgrade.name}</div>
        </div>
        <button
          onClick={handleTap}
          className={`bg-blue-500 text-white px-6 py-3 rounded-full text-lg font-bold mb-4 hover:bg-blue-600 transition-colors ${isMining ? 'bg-blue-600' : ''} ${isShaking ? 'shake' : ''}`}
        >
          TAP TO MINE
        </button>
        <div className="absolute inset-0 pointer-events-none">
          <CoinDrop isActive={showCoinDrop} />
        </div>
      </div>
      <div className="flex space-x-2">
        <button onClick={claimTon} className="bg-blue-900 text-blue-300 px-4 py-2 rounded flex-1 text-sm">CLAIM TON</button>
        <button 
          onClick={() => setIsUpgradePopupOpen(true)}
          className="bg-blue-500 text-white px-4 py-2 rounded flex-1 text-sm hover:bg-blue-600"
        >
          UPGRADE MINER
        </button>
      </div>
    </div>
  );

