import React, { useState, useEffect } from 'react';
import CelebrationAnimation from './CelebrationAnimation';

interface Question {
  question: string;
  answers: string[];
  correctAnswer: number;
}

// Mock API function to generate random questions
const generateRandomQuestion = (): Question => {
  const questions = [
    {
      question: "What is the main purpose of blockchain technology?",
      answers: [
        "To create digital currencies",
        "To provide decentralized and secure record-keeping",
        "To speed up internet connections",
        "To encrypt emails"
      ],
      correctAnswer: 1
    },
    {
      question: "What is a smart contract?",
      answers: [
        "A legally binding document",
        "An AI-powered agreement",
        "Self-executing code on a blockchain",
        "A contract written by a lawyer"
      ],
      correctAnswer: 2
    },
    {
      question: "What is the purpose of mining in cryptocurrency networks?",
      answers: [
        "To create new coins",
        "To validate and process transactions",
        "To increase the value of the currency",
        "Both A and B"
      ],
      correctAnswer: 3
    }
  ];
  return questions[Math.floor(Math.random() * questions.length)];
};

const MissionTab: React.FC = () => {
  const [currentQuestion, setCurrentQuestion] = useState<Question | null>(null);
  const [selectedAnswer, setSelectedAnswer] = useState<number | null>(null);
  const [result, setResult] = useState<string | null>(null);
  const [totalTON, setTotalTON] = useState<number>(0);
  const [showCelebration, setShowCelebration] = useState(false);

  useEffect(() => {
    // Load a new question daily
    const loadNewQuestion = () => {
      setCurrentQuestion(generateRandomQuestion());
      setSelectedAnswer(null);
      setResult(null);
    };

    loadNewQuestion();

    // Set up a timer to load a new question every 24 hours
    const timer = setInterval(loadNewQuestion, 24 * 60 * 60 * 1000);

    return () => clearInterval(timer);
  }, []);

  const handleAnswerSelection = (index: number) => {
    setSelectedAnswer(index);
  };

  const handleSubmit = () => {
    if (selectedAnswer === null || !currentQuestion) return;

    if (selectedAnswer === currentQuestion.correctAnswer) {
      const reward = 0.1; // 0.1 TON reward for correct answer
      setResult(`Correct! You earned ${reward} TON.`);
      setTotalTON(prevTON => prevTON + reward);
      setShowCelebration(true);
      setTimeout(() => setShowCelebration(false), 3000);
    } else {
      setResult(`Incorrect. The correct answer was: ${currentQuestion.answers[currentQuestion.correctAnswer]}`);
    }
  };

  if (!currentQuestion) {
    return <div>Loading...</div>;
  }

  return (
    <div className="p-4 bg-gray-900 min-h-screen">
      <h2 className="text-2xl font-bold mb-4 text-white">Daily Quiz</h2>
      <div className="mb-4 text-yellow-400">Total TON Earned: {totalTON.toFixed(2)} TON</div>
      <div className="bg-gray-800 p-4 rounded-lg mb-4">
        <h3 className="text-xl mb-2 text-white">{currentQuestion.question}</h3>
        {currentQuestion.answers.map((answer, index) => (
          <button
            key={index}
            onClick={() => handleAnswerSelection(index)}
            className={`block w-full text-left p-2 mb-2 rounded ${
              selectedAnswer === index ? 'bg-blue-600' : 'bg-gray-700'
            } text-white`}
          >
            {answer}
          </button>
        ))}
      </div>
      <button
        onClick={handleSubmit}
        disabled={selectedAnswer === null}
        className={`w-full p-2 rounded ${
          selectedAnswer === null ? 'bg-gray-600' : 'bg-green-600'
        } text-white`}
      >
        Submit Answer
      </button>
      {result && (
        <div className={`mt-4 p-2 rounded ${
          result.startsWith('Correct') ? 'bg-green-700' : 'bg-red-700'
        } text-white`}>
          {result}
        </div>
      )}
      {showCelebration && <CelebrationAnimation />}
    </div>
  );
};

export default MissionTab;
