import React, { useState, useEffect } from 'react';

interface CoinDropProps {
  isActive: boolean;
}

const CoinDrop: React.FC<CoinDropProps> = ({ isActive }) => {
  const [coins, setCoins] = useState<{ id: number; x: number; y: number; speed: number }[]>([]);

  useEffect(() => {
    if (isActive) {
      const newCoins = Array.from({ length: 5 }, (_, index) => ({
        id: Date.now() + index,
        x: Math.random() * 100,
        y: -20,
        speed: 2 + Math.random() * 3,
      }));
      setCoins(prevCoins => [...prevCoins, ...newCoins]);
    }
  }, [isActive]);

  useEffect(() => {
    const interval = setInterval(() => {
      setCoins(prevCoins =>
        prevCoins
          .map(coin => ({ ...coin, y: coin.y + coin.speed }))
          .filter(coin => coin.y < 120)
      );
    }, 50);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="coin-drop">
      {coins.map(coin => (
        <div
          key={coin.id}
          className="coin"
          style={{
            left: `${coin.x}%`,
            top: `${coin.y}%`,
          }}
        />
      ))}
    </div>
  );
};

export default CoinDrop;
