'use client'
import React, { useState, useEffect } from 'react';
import CoinDrop from './NeonThunder';

const upgradeData = [
  { name: "Free", hashrate: "2 GH/s", rentPeriod: 90, rentPrice: 0, dailyReturn: 0.02 },
  { name: "TON Silver", hashrate: "20 GH/s", rentPeriod: 90, rentPrice: 1, dailyReturn: 0.2 },
  { name: "TON Gold", hashrate: "100 GH/s", rentPeriod: 90, rentPrice: 5, dailyReturn: 1 },
  { name: "TON Diamond", hashrate: "600 GH/s", rentPeriod: 90, rentPrice: 25, dailyReturn: 6 },
  { name: "TON Platinum", hashrate: "1300 GH/s", rentPeriod: 90, rentPrice: 50, dailyReturn: 13 },
  { name: "TON VIP", hashrate: "3000 GH/s", rentPeriod: 90, rentPrice: 100, dailyReturn: 30 },
];

interface UpgradePlan {
  name: string;
  hashrate: string;
  rentPeriod: number;
  rentPrice: number;
  dailyReturn: number;
}

interface UpgradeMinerPopupProps {
  isOpen: boolean;
  onClose: () => void;
  onSelectPlan: (plan: UpgradePlan) => void;
  currentPlan: UpgradePlan;
}

const UpgradeMinerPopup: React.FC<UpgradeMinerPopupProps> = ({ isOpen, onClose, onSelectPlan, currentPlan }) => {
  const [selectedPlan, setSelectedPlan] = useState<UpgradePlan | null>(null);

  if (!isOpen) return null;

  const handleSelectPlan = (plan: UpgradePlan) => {
    setSelectedPlan(plan);
  };

  const handleConfirm = () => {
    if (selectedPlan) {
      onSelectPlan(selectedPlan);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-gray-900 p-6 rounded-lg max-w-md w-full">
        <h2 className="text-xl font-bold mb-4 text-white text-center">Upgrade Miner</h2>
        <p className="text-sm text-gray-400 text-center mb-4">
          You will be able to earn approximately 12% profit per day and 1700% profit in 30 days by renting a Turbo.
        </p>
        <div className="grid grid-cols-2 gap-4 mb-4">
          {upgradeData.map((plan, index) => (
            <button
              key={index}
              onClick={() => handleSelectPlan(plan)}
              className={`p-2 rounded-lg text-left ${
                selectedPlan === plan ? 'bg-blue-600' : 'bg-gray-800'
              }`}
            >
              <div className="font-bold text-white">{plan.name}</div>
              <div className="text-yellow-500">{plan.hashrate}</div>
            </button>
          ))}
        </div>
        {selectedPlan && (
          <div className="bg-gray-800 p-4 rounded-lg mb-4">
            <div className="flex justify-between mb-2">
              <span className="text-gray-400">Mining Power</span>
              <span className="text-white">{selectedPlan.hashrate}</span>
            </div>
            <div className="flex justify-between mb-2">
              <span className="text-gray-400">Rent Period</span>
              <span className="text-white">{selectedPlan.rentPeriod} days</span>
            </div>
            <div className="flex justify-between mb-2">
              <span className="text-gray-400">30 Days Profit</span>
              <span className="text-yellow-500">{(selectedPlan.dailyReturn * 30).toFixed(2)} TON</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-400">Daily</span>
              <span className="text-yellow-500">{selectedPlan.dailyReturn.toFixed(2)} TON</span>
            </div>
          </div>
        )}
        <div className="flex justify-between items-center mb-4">
          <span className="text-gray-400">Total Renting Price</span>
          <span className="text-white text-xl font-bold">
            {selectedPlan ? selectedPlan.rentPrice.toFixed(2) : '0'} TON
          </span>
        </div>
        <div className="flex space-x-2">
          <button
            onClick={onClose}
            className="bg-gray-700 text-white px-4 py-2 rounded flex-1 text-sm"
          >
            Cancel
          </button>
          <button
            onClick={handleConfirm}
            className="bg-blue-500 text-white px-4 py-2 rounded flex-1 text-sm"
            disabled={!selectedPlan}
          >
            Start Mining
          </button>
        </div>
      </div>
    </div>
  );
};

const ReferralTab: React.FC = () => {
  const [showReferralPopup, setShowReferralPopup] = useState(false);
  const [referralLink, setReferralLink] = useState('');
  const [copySuccess, setCopySuccess] = useState(false);
  const [referrals, setReferrals] = useState(0);
  const [earnedRewards, setEarnedRewards] = useState(0);

  useEffect(() => {
    // Generate a mock referral link
    setReferralLink(`https://ton-mining.app/ref/${Math.random().toString(36).substr(2, 8)}`);
  }, []);

  const handleInvite = () => {
    setShowReferralPopup(true);
  };

  const handleCopyLink = async () => {
    try {
      await navigator.clipboard.writeText(referralLink);
      setCopySuccess(true);
      setTimeout(() => setCopySuccess(false), 2000);
    } catch (err) {
      console.error('Failed to copy text: ', err);
    }
  };

  const referralTiers = [
    { friends: 5, reward: 0.5 },
    { friends: 10, reward: 3 },
    { friends: 25, reward: 8 },
    { friends: 50, reward: 25 },
    { friends: 100, reward: 60 },
  ];

  return (
    <div className="p-4 bg-black h-full">
      <div className="flex justify-between mb-4">
        <button
          onClick={handleInvite}
          className="bg-blue-500 text-white px-4 py-2 rounded flex-1 mr-2 text-sm hover:bg-blue-600"
        >
          Invite
        </button>
        <button className="bg-gray-700 text-white px-4 py-2 rounded flex-1 text-sm">
          Reward: {earnedRewards.toFixed(2)} TON
        </button>
      </div>
      <div className="mb-4 p-4 bg-gray-800 rounded-lg">
        <h3 className="text-lg font-bold mb-2">Your Referrals</h3>
        <p className="text-sm text-gray-400">Total Referrals: {referrals}</p>
        <p className="text-sm text-gray-400">Total Earned: {earnedRewards.toFixed(2)} TON</p>
      </div>
      <div className="text-sm mb-2 text-gray-400">Referral Tiers</div>
      {referralTiers.map((tier, index) => (
        <div key={index} className="flex items-center mb-2 bg-gray-800 rounded p-2">
          <div className="w-8 h-8 bg-blue-500 rounded-full mr-2 flex items-center justify-center text-white">
            {tier.friends}
          </div>
          <div className="flex-1 text-sm">Invite {tier.friends} Friends - Earn {tier.reward} TON</div>
        </div>
      ))}
      {showReferralPopup && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-gray-800 p-6 rounded-lg max-w-md w-full">
            <h2 className="text-xl font-bold mb-4 text-white">Your Referral Link</h2>
            <div className="bg-gray-700 p-2 rounded mb-4 flex items-center">
              <input
                type="text"
                value={referralLink}
                readOnly
                className="bg-transparent text-white flex-1 outline-none"
              />
              <button
                onClick={handleCopyLink}
                className="ml-2 bg-blue-500 text-white px-3 py-1 rounded text-sm hover:bg-blue-600"
              >
                {copySuccess ? 'Copied!' : 'Copy'}
              </button>
            </div>
            <p className="text-sm text-gray-400 mb-4">
              Share this link with your friends. When they sign up and start mining, you'll earn rewards!
            </p>
            <button
              onClick={() => setShowReferralPopup(false)}
              className="bg-gray-700 text-white px-4 py-2 rounded w-full text-sm hover:bg-gray-600"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

const HomeTab: React.FC<{
  currentTon: number;
  minedTon: number;
  currentPlan: UpgradePlan;
  taps: number;
  handleTap: () => void;
  claimTon: () => void;
  setIsUpgradePopupOpen: (isOpen: boolean) => void;
  isMining: boolean;
