'use client'
import React, { useState, useEffect } from 'react';
import CoinDrop from './NeonThunder';

const upgradeData = [
  { name: "Free", hashrate: "2 GH/s", rentPeriod: 90, rentPrice: 0, dailyReturn: 0.02 },
  { name: "TON Silver", hashrate: "20 GH/s", rentPeriod: 90, rentPrice: 1, dailyReturn: 0.2 },
  { name: "TON Gold", hashrate: "100 GH/s", rentPeriod: 90, rentPrice: 5, dailyReturn: 1 },
  { name: "TON Diamond", hashrate: "600 GH/s", rentPeriod: 90, rentPrice: 25, dailyReturn: 6 },
  { name: "TON Platinum", hashrate: "1300 GH/s", rentPeriod: 90, rentPrice: 50, dailyReturn: 13 },
  { name: "TON VIP", hashrate: "3000 GH/s", rentPeriod: 90, rentPrice: 100, dailyReturn: 30 },
];

interface UpgradePlan {
  name: string;
  hashrate: string;
  rentPeriod: number;
  rentPrice: number;
  dailyReturn: number;
}

interface UpgradeMinerPopupProps {
  isOpen: boolean;
  onClose: () => void;
  onSelectPlan: (plan: UpgradePlan) => void;
  currentPlan: UpgradePlan;
}

const UpgradeMinerPopup: React.FC<UpgradeMinerPopupProps> = ({ isOpen, onClose, onSelectPlan, currentPlan }) => {
  const [selectedPlan, setSelectedPlan] = useState<UpgradePlan | null>(null);

  if (!isOpen) return null;

  const handleSelectPlan = (plan: UpgradePlan) => {
    setSelectedPlan(plan);
  };

  const handleConfirm = () => {
    if (selectedPlan) {
      onSelectPlan(selectedPlan);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-gray-900 p-6 rounded-lg max-w-md w-full">
        <h2 className="text-xl font-bold mb-4 text-white text-center">Upgrade Miner</h2>
        <p className="text-sm text-gray-400 text-center mb-4">
          You will be able to earn approximately 12% profit per day and 1700% profit in 30 days by renting a Turbo.
        </p>
        <div className="grid grid-cols-2 gap-4 mb-4">
          {upgradeData.map((plan, index) => (
            <button
              key={index}
              onClick={() => handleSelectPlan(plan)}
              className={`p-2 rounded-lg text-left ${
                selectedPlan === plan ? 'bg-blue-600' : 'bg-gray-800'
              }`}
            >
              <div className="font-bold text-white">{plan.name}</div>
              <div className="text-yellow-500">{plan.hashrate}</div>
            </button>
          ))}
        </div>
        {selectedPlan && (
          <div className="bg-gray-800 p-4 rounded-lg mb-4">
            <div className="flex justify-between mb-2">
              <span className="text-gray-400">Mining Power</span>
              <span className="text-white">{selectedPlan.hashrate}</span>
            </div>
            <div className="flex justify-between mb-2">
              <span className="text-gray-400">Rent Period</span>
              <span className="text-white">{selectedPlan.rentPeriod} days</span>
            </div>
            <div className="flex justify-between mb-2">
              <span className="text-gray-400">30 Days Profit</span>
              <span className="text-yellow-500">{(selectedPlan.dailyReturn * 30).toFixed(2)} TON</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-400">Daily</span>
              <span className="text-yellow-500">{selectedPlan.dailyReturn.toFixed(2)} TON</span>
            </div>
          </div>
        )}
        <div className="flex justify-between items-center mb-4">
          <span className="text-gray-400">Total Renting Price</span>
          <span className="text-white text-xl font-bold">
            {selectedPlan ? selectedPlan.rentPrice.toFixed(2) : '0'} TON
          </span>
        </div>
        <div className="flex space-x-2">
          <button
            onClick={onClose}
            className="bg-gray-700 text-white px-4 py-2 rounded flex-1 text-sm"
          >
            Cancel
          </button>
          <button
            onClick={handleConfirm}
            className="bg-blue-500 text-white px-4 py-2 rounded flex-1 text-sm"
            disabled={!selectedPlan}
          >
            Start Mining
          </button>
        </div>
      </div>
    </div>
  );
};

const CryptoMiningApp: React.FC = () => {
  const [currentTab, setCurrentTab] = useState('Home');
  const [currentTon, setCurrentTon] = useState(0);
  const [minedTon, setMinedTon] = useState(0);
  const [shib, setShib] = useState(0);
  const [taps, setTaps] = useState(0);
  const [currentPlan, setCurrentPlan] = useState(upgradeData[0]);
  const [isMining, setIsMining] = useState(false);
  const [showCoinDrop, setShowCoinDrop] = useState(false);
  const [isShaking, setIsShaking] = useState(false);
  const [isUpgradePopupOpen, setIsUpgradePopupOpen] = useState(false);

  useEffect(() => {
    const interval = setInterval(() => {
      setMinedTon(prevTon => prevTon + currentPlan.dailyReturn / (24 * 60 * 60));
    }, 1000);
    return () => clearInterval(interval);
  }, [currentPlan]);

  const claimTon = () => {
    setCurrentTon(prevTon => prevTon + minedTon);
    setMinedTon(0);
  };

  const handleTap = () => {
    setTaps(prevTaps => prevTaps + 1);
    setMinedTon(prevTon => prevTon + currentPlan.dailyReturn / (24 * 60 * 60));
    setIsMining(true);
    setShowCoinDrop(true);
    setIsShaking(true);
    setTimeout(() => {
      setIsMining(false);
      setIsShaking(false);
    }, 100);
    setTimeout(() => {
      setShowCoinDrop(false);
    }, 1000);
  };

  const handleUpgrade = (plan: UpgradePlan) => {
    if (currentTon >= plan.rentPrice) {
      setCurrentTon(prevTon => prevTon - plan.rentPrice);
      setCurrentPlan(plan);
      setIsUpgradePopupOpen(false);
    }
  };

  const TabButton: React.FC<{ icon: React.FC, label: string }> = ({ icon: Icon, label }) => (
    <button
      onClick={() => setCurrentTab(label)}
      className={`flex flex-col items-center justify-center w-1/4 py-2 ${
        currentTab === label ? 'text-blue-500' : 'text-gray-500'
      }`}
    >
      <Icon />
      <span className="text-xs mt-1">{label}</span>
    </button>
  );

  const HomeIcon: React.FC = () => (
    <svg viewBox="0 0 24 24" width="20" height="20" stroke="currentColor" strokeWidth="2" fill="none">
      <path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z"></path>
      <polyline points="9 22 9 12 15 12 15 22"></polyline>
    </svg>
  );

  const UsersIcon: React.FC = () => (
    <svg viewBox="0 0 24 24" width="20" height="20" stroke="currentColor" strokeWidth="2" fill="none">
      <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"></path>
      <circle cx="9" cy="7" r="4"></circle>
      <path d="M23 21v-2a4 4 0 0 0-3-3.87"></path>
      <path d="M16 3.13a4 4 0 0 1 0 7.75"></path>
    </svg>
  );

  const ClipboardIcon: React.FC = () => (
    <svg viewBox="0 0 24 24" width="20" height="20" stroke="currentColor" strokeWidth="2" fill="none">
      <path d="M16 4h2a2 2 0 0 1 2 2v14a2 2 0 0 1-2 2H6a2 2 0 0 1-2-2V6a2 2 0 0 1 2-2h2"></path>
      <rect x="8" y="2" width="8" height="4" rx="1" ry="1"></rect>
    </svg>
  );

