import React, { useState, useEffect } from 'react';

interface NeonThunderProps {
  isActive: boolean;
}

const NeonThunder: React.FC<NeonThunderProps> = ({ isActive }) => {
  const [opacity, setOpacity] = useState(0);

  useEffect(() => {
    if (isActive) {
      setOpacity(1);
      const timer = setTimeout(() => setOpacity(0), 500);
      return () => clearTimeout(timer);
    }
  }, [isActive]);

  return (
    <div
      className={`neon-thunder ${isActive ? 'active' : ''}`}
      style={{ opacity }}
    >
      <svg width="100" height="100" viewBox="0 0 100 100">
        <path
          d="M50 5 L60 40 L90 50 L60 60 L50 95 L40 60 L10 50 L40 40 Z"
          fill="none"
          stroke="currentColor"
          strokeWidth="2"
        />
      </svg>
    </div>
  );
};

export default NeonThunder;
