'use client'
import React, { useState, useEffect, useRef } from 'react';

const CryptoMiningApp: React.FC = () => {
  const [currentTab, setCurrentTab] = useState('Home');
  const [currentTon, setCurrentTon] = useState(0);
  const [minedTon, setMinedTon] = useState(0);
  const [shib, setShib] = useState(0);
  const [taps, setTaps] = useState(0);
  const [tonPerTap, setTonPerTap] = useState(0.000001);
  const [neonEffect, setNeonEffect] = useState(false);
  const [neonColor, setNeonColor] = useState('');
  const fanRef = useRef<SVGSVGElement>(null);

  useEffect(() => {
    const interval = setInterval(() => {
      setMinedTon(prevTon => prevTon + 0.000000001);
    }, 300);
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    if (fanRef.current) {
      fanRef.current.style.animation = 'spin 3s linear infinite';
    }
  }, []);

  const claimTon = () => {
    setCurrentTon(prevTon => prevTon + minedTon);
    setMinedTon(0);
  };

  const handleTap = () => {
    setTaps(prevTaps => prevTaps + 1);
    setMinedTon(prevTon => prevTon + tonPerTap);
    triggerNeonEffect();
  };

  const triggerNeonEffect = () => {
    const colors = ['#ff00ff', '#00ffff', '#ffff00', '#ff0000', '#00ff00', '#0000ff'];
    const randomColor = colors[Math.floor(Math.random() * colors.length)];
    setNeonColor(randomColor);
    setNeonEffect(true);
    setTimeout(() => setNeonEffect(false), 300);
  };

  const TabButton: React.FC<{ icon: React.FC, label: string }> = ({ icon: Icon, label }) => (
    <button
      onClick={() => setCurrentTab(label)}
      className={`flex flex-col items-center justify-center w-1/4 py-2 ${
        currentTab === label ? 'text-blue-500' : 'text-gray-500'
      }`}
    >
      <Icon />
      <span className="text-xs mt-1">{label}</span>
    </button>
  );

  const HomeIcon: React.FC = () => (
    <svg viewBox="0 0 24 24" width="20" height="20" stroke="currentColor" strokeWidth="2" fill="none">
      <path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z"></path>
      <polyline points="9 22 9 12 15 12 15 22"></polyline>
    </svg>
  );

  const UsersIcon: React.FC = () => (
    <svg viewBox="0 0 24 24" width="20" height="20" stroke="currentColor" strokeWidth="2" fill="none">
      <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"></path>
      <circle cx="9" cy="7" r="4"></circle>
      <path d="M23 21v-2a4 4 0 0 0-3-3.87"></path>
      <path d="M16 3.13a4 4 0 0 1 0 7.75"></path>
    </svg>
  );

  const ClipboardIcon: React.FC = () => (
    <svg viewBox="0 0 24 24" width="20" height="20" stroke="currentColor" strokeWidth="2" fill="none">
      <path d="M16 4h2a2 2 0 0 1 2 2v14a2 2 0 0 1-2 2H6a2 2 0 0 1-2-2V6a2 2 0 0 1 2-2h2"></path>
      <rect x="8" y="2" width="8" height="4" rx="1" ry="1"></rect>
    </svg>
  );

  const FileTextIcon: React.FC = () => (
    <svg viewBox="0 0 24 24" width="20" height="20" stroke="currentColor" strokeWidth="2" fill="none">
      <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z"></path>
      <polyline points="14 2 14 8 20 8"></polyline>
      <line x1="16" y1="13" x2="8" y2="13"></line>
      <line x1="16" y1="17" x2="8" y2="17"></line>
      <polyline points="10 9 9 9 8 9"></polyline>
    </svg>
  );

  const HomeTab: React.FC = () => (
    <div className="p-4 flex flex-col h-full">
      <div className="flex justify-between mb-4">
        <div className="flex items-center">
          <div className="w-8 h-8 bg-blue-500 rounded-full flex items-center justify-center text-white mr-2">
            <svg viewBox="0 0 24 24" width="20" height="20" stroke="currentColor" strokeWidth="2" fill="none">
              <path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5" />
            </svg>
          </div>
          <div>
            <div className="text-sm font-bold">TON Balance</div>
            <div className="text-xs text-gray-400">{currentTon.toFixed(9)} TON</div>
          </div>
        </div>
        <button className="bg-blue-500 text-white px-4 py-1 rounded text-sm">Withdraw</button>
      </div>
      <div className="flex justify-between mb-4">
        <div className="flex items-center">
          <div className="w-8 h-8 bg-orange-500 rounded-full flex items-center justify-center text-white mr-2">
            <span className="text-xs">SHIB</span>
          </div>
          <div>
            <div className="text-sm font-bold">SHIB</div>
            <div className="text-xs text-gray-400">0.00 SHIB</div>
          </div>
        </div>
        <button className="bg-blue-500 text-white px-4 py-1 rounded text-sm">Withdraw</button>
      </div>
      <div className="flex-grow flex flex-col items-center justify-center">
        <div className={`relative w-48 h-48 mb-4 ${neonEffect ? 'animate-pulse' : ''}`} style={neonEffect ? { boxShadow: `0 0 10px ${neonColor}, 0 0 20px ${neonColor}, 0 0 30px ${neonColor}, 0 0 40px ${neonColor}` } : {}}>
          <div className="absolute inset-0 bg-blue-900 rounded-full"></div>
          <div className="absolute inset-1 bg-black rounded-full flex items-center justify-center">
            <svg ref={fanRef} viewBox="0 0 200 200" width="100%" height="100%">
              <g>
                {[0, 45, 90, 135, 180, 225, 270, 315].map((rotation, index) => (
                  <path key={index} d="M100 100 L85 15 A70 70 0 0 1 115 15 Z" fill="url(#blade-gradient)" transform={`rotate(${rotation} 100 100)`} />
                ))}
              </g>
              <circle cx="100" cy="100" r="20" fill="url(#hub-gradient)" />
              <circle cx="100" cy="100" r="98" stroke="#3182CE" strokeWidth="4" fill="none" />
              <defs>
                <radialGradient id="blade-gradient">
                  <stop offset="0%" stopColor="#718096" />
                  <stop offset="100%" stopColor="#2D3748" />
                </radialGradient>
                <radialGradient id="hub-gradient">
                  <stop offset="0%" stopColor="#A0AEC0" />
                  <stop offset="100%" stopColor="#4A5568" />
                </radialGradient>
              </defs>
            </svg>
          </div>
        </div>
        <div className="text-center mb-4">
          <div className="text-2xl font-bold">{minedTon.toFixed(9)} TON</div>
          <div className="text-sm text-gray-400">Mined (Unclaimed)</div>
          <div className="text-sm text-gray-400">Hashrate: 2 Ghz</div>
          <div className="text-sm text-gray-400">Taps: {taps}</div>
          <div className="text-sm text-gray-400">TON per tap: {tonPerTap.toFixed(6)}</div>
        </div>
        <button
          onClick={handleTap}
          className="bg-blue-500 text-white px-6 py-3 rounded-full text-lg font-bold mb-4 hover:bg-blue-600 transition-colors"
        >
          TAP TO MINE
        </button>
      </div>
      <div className="flex space-x-2">
        <button onClick={claimTon} className="bg-blue-900 text-blue-300 px-4 py-2 rounded flex-1 text-sm">CLAIM TON</button>
        <button className="bg-blue-500 text-white px-4 py-2 rounded flex-1 text-sm">UPGRADE MINER</button>
      </div>
    </div>
  );

  const ReferralTab: React.FC = () => (
    <div className="p-4 bg-black h-full">
      <div className="flex justify-between mb-4">
        <button className="bg-gray-800 text-white px-4 py-2 rounded flex-1 mr-2 text-sm">Invite</button>
        <button className="bg-blue-500 text-white px-4 py-2 rounded flex-1 text-sm">Reward</button>
      </div>
      {[
        { friends: 3, reward: 1 },
        { friends: 5, reward: 2 },
        { friends: 10, reward: 5 },
        { friends: 20, reward: 20 },
        { friends: 50, reward: 100 },
      ].map((item, index) => (
        <div key={index} className="flex items-center mb-2 bg-gray-800 rounded p-2">
          <div className="w-8 h-8 bg-blue-500 rounded-full mr-2 flex items-center justify-center">
            <UsersIcon />
          </div>
          <div className="flex-1 text-sm">Invite {item.friends} Friends - Earn {item.reward} TON</div>
        </div>
      ))}
    </div>
  );

  const MissionTab: React.FC = () => (
    <div className="p-4 bg-black h-full">
      <div className="mb-4">
        <h2 className="text-orange-500 text-lg mb-2">SHIB Earn</h2>
        <p className="text-sm mb-2 text-gray-400">You will receive SHIB instantly after completing each task</p>
        <p className="text-sm text-gray-400">Task available 4</p>
      </div>
      <div className="text-sm mb-2 text-gray-400">TASKS</div>
      {[
        { task: 'Join Telegram Earn', reward: 5000 },
        { task: 'Follow Twitter Earn', reward: 10 },
        { task: 'Follow Instagram Earn', reward: 10 },
        { task: 'Subscribe YouTube Earn', reward: 10 },
      ].map((item, index) => (
        <div key={index} className="flex justify-between items-center py-2 border-b border-gray-800">
          <div className="text-sm">{item.task}</div>
          <div className="flex items-center">
            <span className="text-orange-500 mr-1 text-sm">+{item.reward}</span>
            <svg viewBox="0 0 24 24" width="16" height="16" stroke="currentColor" strokeWidth="2" fill="none" className="text-orange-500">
              <path d="M9 5l7 7-7 7" />
            </svg>
          </div>
        </div>
      ))}
      <div className="mt-6 bg-gray-900 p-4 rounded-lg">
        <h2 className="text-orange-500 text-lg mb-2">Daily Quiz</h2>
        <div className="flex items-center mb-2">
          <div className="w-6 h-6 bg-yellow-500 rounded-full mr-2"></div>
          <span className="text-sm">0 Coins</span>
        </div>
        <p className="text-sm mb-2 text-gray-400">Solve & Earn 500K Coins Everyday</p>
        <p className="text-sm mb-2">What is Ethereum primarily used for?</p>
        <button className="bg-gray-800 text-white px-4 py-2 rounded w-full mb-2 text-sm text-left">Email services</button>
        <button className="bg-gray-800 text-white px-4 py-2 rounded w-full mb-2 text-sm text-left">Smart contracts and decentralized applications</button>
        <button className="bg-gray-800 text-white px-4 py-2 rounded w-full text-sm text-left">Cloud storage</button>
      </div>
    </div>
  );

  const HistoryTab: React.FC = () => (
    <div className="p-4 text-center bg-black h-full">
      <p className="text-gray-400">No transactions made yet.</p>
    </div>
  );

  return (
    <div className="bg-black text-white h-full flex flex-col relative">
      <div className="flex-1 overflow-y-auto">
        {currentTab === 'Home' && <HomeTab />}
        {currentTab === 'Referral' && <ReferralTab />}
        {currentTab === 'Mission' && <MissionTab />}
        {currentTab === 'History' && <HistoryTab />}
      </div>
      <div className="flex border-t border-gray-800">
        <TabButton icon={HomeIcon} label="Home" />
        <TabButton icon={UsersIcon} label="Referral" />
        <TabButton icon={ClipboardIcon} label="Mission" />
        <TabButton icon={FileTextIcon} label="History" />
      </div>
    </div>
  );
};

// Main App component
const App: React.FC = () => (
  <div className="h-screen w-screen bg-black flex items-center justify-center">
    <div className="w-full max-w-md h-full bg-black text-white overflow-hidden">
      <CryptoMiningApp />
    </div>
  </div>
);

export default App;

// Add this to your global CSS file (e.g., index.css or global.css)
/*
@keyframes spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}
*/
